mapper.select:
SELECT name_thing,start_time
FROM demos.todo_quest
    join demos.todo_group_quest on demos.todo_quest.quest_id = todo_group_quest.quest_id
WHERE todo_group_quest.group_id = #{groupId}
  AND finish = false;

SELECT name_thing,start_time
FROM demos.todo_quest
    JOIN demos.todo_group_quest on demos.todo_quest.quest_id = todo_group_quest.quest_id
WHERE todo_group_quest.group_id = #{groupId}
  AND finish = true;
